import { Link, useLocation } from "react-router-dom";
import "./movie.css";
import { Publish } from "@mui/icons-material";
import { MovieContext } from "../../context/movieContext/MovieContext";
import { useContext, useEffect, useState } from "react";
import { updateMovie } from "../../context/movieContext/apiCalls";
import { getDownloadURL, getStorage, ref, uploadBytesResumable } from "firebase/storage";
import app from "../../firebase";


export default function Movie() {
  const location = useLocation();
  const [movie, setMovie] = useState(location.state.movie);
  const { dispatch } = useContext(MovieContext);
  const [uploaded, setUploaded] = useState(0);
  const [inputs, setInputs] = useState({
    title: "",
    year: "",
    genre: "",
    limit: "",
    trailer: null,
    video: null,
    img: null,
  });

  useEffect(() => {
    setInputs({
      title: movie?.title || "",
      year: movie?.year || "",
      genre: movie?.genre || "",
      limit: movie?.limit || "",
      trailer: null,
      video: null,
      img: null,
    });
  }, [movie]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const updatedMovie = await updateMovie(movie._id, inputs, dispatch);
      setMovie(updatedMovie);
      alert("Movie updated successfully!");
    } catch (error) {
      console.log(error);
      alert("Failed to update movie.");
    }
  };

  const handleChange = (e) => {
    setInputs((prev) => {
      return { ...prev, [e.target.name]: e.target.value };
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setInputs((prev) => {
      return { ...prev, [e.target.name]: file };
    });
  };

  const handleUpload = () => {
    const storage = getStorage(app);

    // Upload trailer
    if (inputs.trailer) {
      const trailerFileName = new Date().getTime() + inputs.trailer.name;
      const trailerStorageRef = ref(storage, `/items/${trailerFileName}`);
      const trailerUploadTask = uploadBytesResumable(trailerStorageRef, inputs.trailer);

      trailerUploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          console.log("Trailer Upload is " + progress + "% done");
        },
        (error) => {
          console.log(error);
        },
        () => {
          getDownloadURL(trailerUploadTask.snapshot.ref).then((downloadURL) => {
            setInputs((prev) => {
              return { ...prev, trailer: downloadURL };
            });
            setUploaded((prev) => prev + 1);
          });
        }
      );
    }

    // Upload video
    if (inputs.video) {
      const videoFileName = new Date().getTime() + inputs.video.name;
      const videoStorageRef = ref(storage, `/items/${videoFileName}`);
      const videoUploadTask = uploadBytesResumable(videoStorageRef, inputs.video);

      videoUploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          console.log("Video Upload is " + progress + "% done");
        },
        (error) => {
          console.log(error);
        },
        () => {
          getDownloadURL(videoUploadTask.snapshot.ref).then((downloadURL) => {
            setInputs((prev) => {
              return { ...prev, video: downloadURL };
            });
            setUploaded((prev) => prev + 1);
          });
        }
      );
    }

    // Upload image
    if (inputs.img) {
      const imageFileName = new Date().getTime() + inputs.img.name;
      const imageStorageRef = ref(storage, `/items/${imageFileName}`);
      const imageUploadTask = uploadBytesResumable(imageStorageRef, inputs.img);

      imageUploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          console.log("Image Upload is " + progress + "% done");
        },
        (error) => {
          console.log(error);
        },
        () => {
          getDownloadURL(imageUploadTask.snapshot.ref).then((downloadURL) => {
            setInputs((prev) => {
              return { ...prev, img: downloadURL };
            });
            setUploaded((prev) => prev + 1);
          });
        }
      );
    }
  }

  useEffect(() => {
    if (uploaded === 3) {
      handleUpdate();
      setUploaded(0);
    }
  }, [uploaded]);

  return (
    <div className="product">
      <div className="productTitleContainer">
        <h1 className="productTitle">Movie</h1>
        <Link to="/newMovie">
          <button className="productAddButton">Create</button>
        </Link>
      </div>
      <div className="productTop">
        <div className="productTopRight">
          <div className="productInfoTop">
            <img src={movie?.img} alt="" className="productInfoImg" />
            <span className="productName">{movie?.title}</span>
          </div>
          <div className="productInfoBottom">
            <div className="productInfoItem">
              <span className="productInfoKey">id:</span>
              <span className="productInfoValue">{movie?._id}</span>
            </div>
            <div className="productInfoItem">
              <span className="productInfoKey">genre:</span>
              <span className="productInfoValue">{movie?.genre}</span>
            </div>
            <div className="productInfoItem">
              <span className="productInfoKey">year:</span>
              <span className="productInfoValue">{movie?.year}</span>
            </div>
            <div className="productInfoItem">
              <span className="productInfoKey">limit:</span>
              <span className="productInfoValue">{movie?.limit}</span>
            </div>
          </div>
        </div>
      </div>
      <div className="productBottom">
        <form className="productForm">
          <div className="productFormLeft">
            <label>Movie Title</label>
            <input
              type="text"
              placeholder={movie?.title}
              name="title"
              value={inputs.title}
              onChange={handleChange}
            />
            <label>Year</label>
            <input
              type="text"
              placeholder={movie?.year}
              name="year"
              value={inputs.year}
              onChange={handleChange}
            />
            <label>Genre</label>
            <input
              type="text"
              placeholder={movie?.genre}
              name="genre"
              value={inputs.genre}
              onChange={handleChange}
            />
            <label>Limit</label>
            <input
              type="text"
              placeholder={movie?.limit}
              name="limit"
              value={inputs.limit}
              onChange={handleChange}
            />
            <label>Trailer</label>
            <input
              type="file"
              name="trailer"
              onChange={handleFileChange}
            />
            <label>Video</label>
            <input
              type="file"
              name="video"
              onChange={handleFileChange}
              placeholder={movie?.video}
            />
          </div>
          <div className="productFormRight">
            <div className="productUpload">
              <img
                src={movie?.img}
                alt=""
                className="productUploadImg"
              />
              <label for="file">
                <Publish />
              </label>
              <input
                type="file"
                id="file"
                style={{ display: "none" }}
                name="img"
                onChange={handleFileChange}
              />
            </div>
            <button className="productButton" onClick={handleUpload}>Update</button>
          </div>
        </form>
      </div>
    </div>
  );
}
