import { ArrowDownward, ArrowUpward } from "@mui/icons-material";
import FeaturedInfo from "../../components/featuredInfo/FeaturedInfo";
import "./home.css";
// import { userData } from "../../dummyData";
import WidgetSm from "../../components/widgetSm/WidgetSm";
import WidgetLg from "../../components/widgetLg/WidgetLg";
import { useEffect, useMemo, useState } from "react";
import { makeRequest } from "../../axios";
import { getStorage, ref, listAll, getMetadata } from 'firebase/storage';
import app from "../../firebase";

export default function Home() {
  const [remainingStorageInMB, setRemainingStorageInMB] = useState(null);
  // const MONTHS = useMemo(
  //   () => [
  //     "Jan",
  //     "Feb",
  //     "Mar",
  //     "Apr",
  //     "May",
  //     "Jun",
  //     "Jul",
  //     "Agu",
  //     "Sep",
  //     "Oct",
  //     "Nov",
  //     "Dec",
  //   ],
  //   []
  // );

  const [userStats, setUserStats] = useState([]);

  const calculateTotalFileSize = async () => {
    const storage = getStorage(app);
    const itemsRef = ref(storage, "/items/"); 
    try {
      const listResult = await listAll(itemsRef);
      let totalSize = 0;

      for (const prefix of listResult.prefixes) {
        const prefixRef = ref(storage, prefix.fullPath);
        const prefixListResult = await listAll(prefixRef);

        for (const item of prefixListResult.items) {

          const metadata = await getMetadata(item);
          totalSize += metadata.size;
        }
        // console.log("totalSize", totalSize)
      }

      return totalSize;
    } catch (error) {  
      console.log("Error calculating total file size:", error);
      return 0;
    }
  };

  const calculateTotalStorageLimit = async () => {
    const GBStoredLimit = 5;
    const totalStorageLimit = GBStoredLimit * 1024 * 1024 * 1024; // Convert GB to bytes
    console.log("Total storage limit:", GBStoredLimit + " GB");
    return totalStorageLimit;
  };

  // Usage
  const display = async () => {
    const totalSizeInBytes = await calculateTotalFileSize();
    const totalSizeInMB = await (totalSizeInBytes / (1024 * 1024)); // Convert bytes to megabytes
    const totalStorageLimit = calculateTotalStorageLimit();
    const remainingStorage =
      (totalStorageLimit - totalSizeInBytes) / (1024 * 1024); // Convert bytes to megabytes

    console.log("Total file size:", totalSizeInMB.toFixed(2) + " MB");
    console.log(
      "Total storage limit:",
      (totalStorageLimit / (1024 * 1024 * 1024)).toFixed(2) + " GB"
    );
    console.log("Remaining storage:", remainingStorage.toFixed(2) + " MB");
      setRemainingStorageInMB(remainingStorage);
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      if (remainingStorageInMB === null) {
        display();
      }
    }, 1000);

    return () => clearInterval(intervalId);
  }, [remainingStorageInMB]);


  useEffect(() => {
    const getStats = async () => {
      try {
        const res = await makeRequest.get("users/stats", {
          headers: {
            token:
              "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYwZTZmYzQ2NDk0Mjc3MTYwNDg4MmMxNiIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYyNTgzMjMxMSwiZXhwIjoxNjI2MjY0MzExfQ.ATXV-1TTWIGyVBttTQSf0erRWjsgZ8jHQv1ZsUixbng",
          },
        });
        const statsList = res.data.sort(function (a, b) {
          return a._id - b._id;
        });
        res.data.map((item) =>
          setUserStats((prev) => [
            ...prev,
            { name: MONTHS[item._id - 1], "New User": item.total },
          ])
        );
      } catch (err) {
        console.log(err);
      }
    };
    getStats();
  }, [MONTHS]);
  // console.log("stats: ", userStats);
  return (
    <div className="home">
      <FeaturedInfo />
      <div className="featuredItem">
        <span className="featuredTitle">Revanue</span>
        <div className="featuredMoneyContainer">
          <span className="featuredMoney">$2,415</span>
          <span className="featuredMoneyRate">
            -11.4 <ArrowDownward  className="featuredIcon negative"/>
          </span>
        </div>
        <span className="featuredSub">Compared to last month</span>
      </div>
      <div className="homeWidgets">
        <WidgetSm />
        <WidgetLg />
      </div>
    </div>
  );
}
