import { useContext, useEffect, useState } from "react";
import "./newMovie.css";

import { createMovie } from "../../context/movieContext/apiCalls";
import { MovieContext } from "../../context/movieContext/MovieContext";
import app from "../../firebase";
import { getDownloadURL, getStorage, ref, uploadBytesResumable } from "firebase/storage";
import { CircularProgress, LinearProgress } from "@mui/material";

export default function NewMovie() {
  const [movie, setMovie] = useState(null);
  const [img, setImg] = useState(null);
  const [imgTitle, setImgTitle] = useState(null);
  const [imgSm, setImgSm] = useState(null);
  const [trailer, setTrailer] = useState(null);
  const [video, setVideo] = useState(null);
  const [uploaded, setUploaded] = useState(0);
  const [allInputsPresent, setAllInputsPresent] = useState(false);
  const [uploading, setUploading] = useState(false);

  const { dispatch } = useContext(MovieContext);

  // Add an effect to update the allFilesPresent state whenever one of the file inputs changes
  useEffect(() => {
    setAllInputsPresent(
      !!img &&
        !!imgTitle &&
        !!imgSm &&
        !!trailer &&
        !!video &&
        !!movie?.title
    );
  }, [img, imgTitle, imgSm, trailer, video, movie]);

  useEffect(() => {
    if (uploaded === 5) {
      setImg(null);
      setImgTitle(null);
      setImgSm(null);
      setTrailer(null);
      setVideo(null);
      setUploading(false); // Set uploading to false when the upload is complete
    }
  }, [uploaded]);

  const reset = () => {
    setMovie(null);
    setImg(null);
    setImgTitle(null);
    setImgSm(null);
    setTrailer(null);
    setVideo(null);
    setUploaded(0);
    setAllInputsPresent(false);
  };

  const handleChange = (e) => {
    setMovie({ ...movie, [e.target.name]: e.target.value });
  };

  const upload = (items) => {
    setUploading(true);
    items?.forEach((item) => {
      const storage = getStorage(app);
      const fileName = new Date().getTime() + item.label + item.file.name;
      const storageRef = ref(storage, `/items/${item.label}/${fileName}`);
      // const uploadTask = storage.ref(`/items/${fileName}`).put(item.file);
      console.log("file type:", item.file.type)
      console.log("item.label:", item.label)
      const uploadTask = uploadBytesResumable(storageRef, item.file);
      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          console.log("Upload is " + progress + "% done");
        },
        (error) => {
          console.log(error);
        },
        () => {
          getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
            setMovie((prev) => {
              return { ...prev, [item.label]: downloadURL };
            });
            setUploaded((prev) => prev + 1);
          });
        }
      );
    });
  };

  const handleUpload = (e) => {
    e.preventDefault();
    upload([
      { file: img, label: "img" },
      { file: imgTitle, label: "imgTitle" },
      { file: imgSm, label: "imgSm" },
      { file: trailer, label: "trailer" },
      { file: video, label: "video" },
    ]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("movie",movie);
    createMovie(movie, dispatch).then(() => { // add a then method here
      console.log("first")
      reset(); // move the reset function here
     })
     .catch((error) => {
       // Handle any error that occurs during movie creation
       console.log(error);
     });
  };

  return (
    <div className="newProduct">
      <h1 className="addProductTitle">New Movie</h1>
      <form className="addProductForm">
        <div className="addProductItem">
          <label>Image</label>
          <input
            type="file"
            id="img"
            name="img"
            onChange={(e) => e.target && setImg(e.target.files[0])}
            style={{ border: !img ? "1px solid red" : "" }}
          />
        </div>
        <div className="addProductItem">
          <label>Title image</label>
          <input
            type="file"
            id="imgTitle"
            name="imgTitle"
            onChange={(e) => e.target && setImgTitle(e.target.files[0])}
            style={{ border: !imgTitle ? "1px solid red" : "" }}
          />
        </div>
        <div className="addProductItem">
          <label>Thumbnail image</label>
          <input
            type="file"
            id="imgSm"
            name="imgSm"
            onChange={(e) => e.target && setImgSm(e.target.files[0])}
            style={{ border: !imgSm ? "1px solid red" : "" }}
          />
        </div>
        <div className="addProductItem">
          <label>Title</label>
          <input
            type="text"
            placeholder="Title"
            name="title"
            onChange={handleChange}
            value={movie?.title || ""}
            className={!movie?.title ? "highlight" : ""}
          />
        </div>
        <div className="addProductItem">
          <label>Description</label>
          <input
            type="text"
            placeholder="description"
            name="desc"
            onChange={handleChange}
            value={movie?.desc || ""}
          />
        </div>
        <div className="addProductItem">
          <label>Year</label>
          <input
            type="text"
            placeholder="Year"
            name="year"
            onChange={handleChange}
            value={movie?.year || ""}
          />
        </div>
        <div className="addProductItem">
          <label>Genre</label>
          <input
            type="text"
            placeholder="Genre"
            name="genre"
            onChange={handleChange}
            value={movie?.genre || ""}
          />
        </div>
        <div className="addProductItem">
          <label>Duration</label>
          <input
            type="text"
            placeholder="Duration"
            name="duration"
            onChange={handleChange}
            value={movie?.duration || ""}
          />
        </div>
        <div className="addProductItem">
          <label>Limit</label>
          <input
            type="text"
            placeholder="limit"
            name="limit"
            onChange={handleChange}
            value={movie?.limit || ""}
          />
        </div>
        <div className="addProductItem">
          <label>Is Series?</label>
          <select
            name="isSeries"
            id="isSeries"
            onChange={handleChange}
            value={movie?.isSeries || "false"}
          >
            <option value="false">No</option>
            <option value="true">Yes</option>
          </select>
        </div>
        {uploading ? (
          <LinearProgress
            variant="determinate"
            value={Math.round((uploaded / 5) * 100)}
            style={{ width: "100%", margin: "20px 0" }}
          />
        ) : (
          <button
            className="addProductButton"
            disabled={!allInputsPresent || uploading}
            onClick={handleUpload}
          >
            Upload
          </button>
        )}
        {/* {uploading && <CircularProgress />}
        {!uploading && (
          <button
            className="addProductButton"
            type="submit"
            disabled={!allInputsPresent}
            onClick={handleSubmit}
          >
            Create
          </button>
        )} */}
      </form>
    </div>
  );
}
