import { Link,  useParams } from "react-router-dom";
import "./movie.css";
import { Publish } from "@mui/icons-material";
import { MovieContext } from "../../context/movieContext/MovieContext";
import { useContext, useEffect, useState } from "react";
import { getMovie, updateMovie } from "../../context/movieContext/apiCalls";
import { getDownloadURL, getStorage, ref, uploadBytesResumable } from "firebase/storage";
import app from "../../firebase";
import { makeRequest } from "../../axios";


export default function Movie() {
  const { movieId } = useParams();
  const [movie, setMovie] = useState(null);
  const [imgPerc, setImgPerc] = useState(0);
  const { dispatch } = useContext(MovieContext);
  const [inputs, setInputs] = useState({
    title: "",
    year: "",
    genre: "",
    limit: "",
    trailer: null,
    video: null,
    img: null,
  });

  //getting the movie
  useEffect(() => {
    const fetchMovie = async () => {
      try {
        // Fetch the movie information using the movie ID
        const res = await makeRequest.get(`/movies/find/${movieId}`); // Call the getMovie function with the movie ID and dispatch
        setMovie(res.data);
      } catch (error) {
        console.log(error);
      }
    };

    fetchMovie();
  }, [movieId, dispatch]);

  //initialise the inputs
  useEffect(() => {
    setInputs({
      title: movie?.title || "",
      year: movie?.year || "",
      genre: movie?.genre || "",
      limit: movie?.limit || "",
      trailer: null,
      video: null,
      img: null,
    });
  }, [movie]);

  //send the updated info to the api
  const handleUpdate = async (e) => {
    try {
        const updatedMovie = await updateMovie(movie._id, inputs, dispatch);
        setMovie(updatedMovie);
        alert("Movie updated successfully!");
    } catch (error) {
        console.log(error);
        alert("Failed to update movie.");
    }
  };

  // console.log("movie: ", movie)
  const handleChange = (e) => {
      setInputs((prev) => {
      return { ...prev, [e.target.name]: e.target.value };
    });
  };

  //uploads the image, tailer and video to the storage
  const handleFileUpload = (file, field) => {
    const storage = getStorage(app);
    const fileName = new Date().getTime() + file.name;
    const storageRef = ref(storage, fileName);
    const uploadTask = uploadBytesResumable(storageRef, file);

    uploadTask.on(
      'state_changed',
      (snapshot) => {
        const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        console.log(`Upload is ${progress}% done for ${field}`);
        setImgPerc(Math.round(progress));
        switch (snapshot.state) {
          case 'paused':
            console.log('Upload is paused');
            break;
          case 'running':
            console.log('Upload is running');
            break;
          default:
            break;
        }
      },
      (error) => {
        console.log(error);
      },
      () => {
        getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
          console.log(`${field} available at:`, downloadURL);
          setInputs((prevInputs) => ({ ...prevInputs, [field]: downloadURL }));
          console.log("Input", inputs)
          // Additional code for handling the download URL if needed
        });
      }
    );
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    const field = e.target.name;
    setInputs((prev) => ({ ...prev, [field]: file }));
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    const uploadTasks = [];

    if (inputs.trailer && inputs.trailer instanceof File) {
      uploadTasks.push(handleFileUpload(inputs.trailer, 'trailer'));
    }

    if (inputs.video && inputs.video instanceof File) {
      uploadTasks.push(handleFileUpload(inputs.video, 'video'));
    }

    if (inputs.img && inputs.img instanceof File) {
      uploadTasks.push(handleFileUpload(inputs.img, 'img'));
    }

    await Promise.all(uploadTasks);
      handleUpdate();
    };

  return (
    <div className="product">
      <div className="productTitleContainer">
        <h1 className="productTitle">Movie</h1>
        <Link to="/newMovie">
          <button className="productAddButton">Create</button>
        </Link>
      </div>
      <div className="productTop">
        <div className="productTopRight">
          <div className="productInfoTop">
            <img src={movie?.img} alt="" className="productInfoImg" />
            <span className="productName">{movie?.title}</span>
          </div>
          <div className="productInfoBottom">
            <div className="productInfoItem">
              <span className="productInfoKey">id:</span>
              <span className="productInfoValue">{movie?._id}</span>
            </div>
            <div className="productInfoItem">
              <span className="productInfoKey">genre:</span>
              <span className="productInfoValue">{movie?.genre}</span>
            </div>
            <div className="productInfoItem">
              <span className="productInfoKey">year:</span>
              <span className="productInfoValue">{movie?.year}</span>
            </div>
            <div className="productInfoItem">
              <span className="productInfoKey">limit:</span>
              <span className="productInfoValue">{movie?.limit}</span>
            </div>
          </div>
        </div>
      </div>
      <div className="productBottom">
        <form className="productForm">
          <div className="productFormLeft">
            <label>Movie Title</label>
            <input
              type="text"
              placeholder={movie?.title}
              name="title"
              value={inputs.title}
              onChange={handleChange}
            />
            <label>Year</label>
            <input
              type="text"
              placeholder={movie?.year}
              name="year"
              value={inputs.year}
              onChange={handleChange}
            />
            <label>Genre</label>
            <input
              type="text"
              placeholder={movie?.genre}
              name="genre"
              value={inputs.genre}
              onChange={handleChange}
            />
            <label>Limit</label>
            <input
              type="text"
              placeholder={movie?.limit}
              name="limit"
              value={inputs.limit}
              onChange={handleChange}
            />
            <label>Trailer</label>
            <input
              type="file"
              name="trailer"
              onChange={handleFileChange}
            />
            <label>Video</label>
            <input
              type="file"
              name="video"
              onChange={handleFileChange}
              placeholder={movie?.video}
            />
          </div>
          <div className="productFormRight">
            <div className="productUpload">
              <img
                src={movie?.img}
                alt=""
                className="productUploadImg"
              />
              <label htmlFor="file">
                <Publish />
              </label>
              <input
                type="file"
                id="file"
                style={{ display: "none" }}
                name="img"
                onChange={handleFileChange}
              />
            </div>
            <button className="productButton" onClick={handleUpload}>Update</button>
          </div>
        </form>
      </div>
    </div>
  );
}
